import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

import { Product, products } from '../products';
import { CartService } from '../cart.service';
import { ApiTestService } from '../api-test.service';
import { Observable, of } from 'rxjs';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { catchError, map, tap } from 'rxjs/operators';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})
export class ProductDetailsComponent implements OnInit {
  product: Product | undefined;

  private apiUrl = 'https://www.enricofrison.cloud/api/v1/'; // URL to web api

  constructor(
    private route: ActivatedRoute,
    private cartService: CartService,
    private api: ApiTestService,
    private http: HttpClient
  ) {}

  addToCart(product: Product){
    this.cartService.addToCart(product);
    window.alert('Your product has been aded to the cart!');
  }

  getNows() : Observable<string> {
    return this.http.get<string>(this.apiUrl)
    .pipe(
      tap(_ => console.log('fetched now')),
      catchError(this.handleError<string>('getHeroes', ''))
    );
  }

  private handleError<T>(operation = 'operation', result?: T) {
    return (error: any): Observable<T> => {
      //TODO: send the error to remove logging infratstructure
      console.error(error); // log to console insetead

      //TODO: better job of transforming error for user consumption
      //this.log(`${operation} failed: ${error.message}`);

      // Let the app keep running by returning an empty result
      return of(result as T);
    }
  }

  ngOnInit() {
    //First get the product id from the current route.
    const routeParams = this.route.snapshot.paramMap;
    const productIdFromRoute = Number(routeParams.get('productId'));
    const productUrlNameFromRoute = routeParams.get('productUrlName');

    //this.product = products.find(product => product.id === productIdFromRoute);
    this.product = products.find(product => product.urlName === productUrlNameFromRoute);
  }
}

<div *ngIf="product; else notFound">
    <h2>Product details</h2>
    <h3>{{ product.name }}</h3>
    <h4>{{ product.price }}</h4>
    <p>{{ product.description }}</p>
    <button 
        (click)="addToCart(product)">
        Buy it now!
    </button>
</div>

<ng-template #notFound>
    <h2>Product not found</h2>
    <p>I'm sorry the product you are looking for was not found in our database</p>
</ng-template>
